define("SRC_URL","http://www.concrete5.org/download_file/-/view/79252/");
define("VERSION","Ver.5.7.4.2");
define("FILENAME","./concrete5.7.4.2.zip");
define("DIRNAME","./concrete5.7.4.2");

$messages = array(
    "ja" => array(
        "このディレクトリにconcrete5 ".VERSION." をダウンロードし展開します。",
        "ダウンロード開始",
        "このディレクトリにconcrete5の圧縮ファイルをダウンロード中です。",
        "このディレクトリに圧縮ファイルを展開中です。",
        "ダウンロード、展開しました。",
        "ダウンロード、展開に失敗しました。",
        'インストール画面にジャンプします。\nこのファイルは必ず削除してください。'
    ),
    "en" => array(
        "This program will download & deploy concrete5 ".VERSION." in this directory.",
        "START",
        "Downloading...",
        "Deploying...",
        "Done!",
        "Oops! Sorry, I couldn't complete the process.",
        'Jump to the install page. \n- Please remove this file -'
    )
    //Add translated messages if you want to print your language messages.
);

$lang = "en";//default language
//check browser accept language
$accept_lang = explode(',', $_SERVER['HTTP_ACCEPT_LANGUAGE']);
foreach($accept_lang as $l){
    $l = substr($l,0,2);
    if(array_key_exists($l, $messages)){
        $lang = $l;
        break;
    }
}

if(isset($_GET["step"])){
    switch($_GET["step"]){
        case 1:
            $rfp = fopen(SRC_URL, "r");
            $lfp = fopen(FILENAME,"cb");
            if(!$rfp || !$lfp){
                echo json_encode(0);
                exit;
            }else{
                while($line = fgets($rfp)){
                    fwrite($lfp, $line);
                }
                fclose($rfp);
                fclose($lfp);
                echo json_encode(1);
            }
            break;
        case 2:
    		if (function_exists('zip_open')) {
    			try {
    				$zip = new ZipArchive;
    				if ($zip->open(FILENAME) === TRUE) {
    					$zip->extractTo("./");
    					$zip->close();
    					if($dp = opendir("./".DIRNAME)){
        					while (($file = readdir($dp)) !== false) {
                                if ($file != "." && $file != "..") {
                                    rename("./".DIRNAME."/".$file, "./".$file);
                                }
                            }
                            closedir($dp);
    					}
    					echo json_encode(1);
    				}
    			} catch(Exception $e) {
    			    echo json_encode(0);
    			}
    			exit;
     		}
            exec("unzip ".FILENAME);
            exec("mv ./".DIRNAME."/* ./");
            exec("rm -Rf ./".DIRNAME);

            echo json_encode(1);
            break;
        default:
            echo json_encode(0);
            break;
    }
}else{
?><!DOCTYPE html>
