<h1 class="center">concrete5 <?php echo VERSION; ?> Downloader</h1>
<p class="center"><?php echo $messages[$lang][0]; ?></p>
<p style="line-height:3em;" class="center"><a href="#" class="btn start"><?php echo $messages[$lang][1]; ?></a></p>
<p class="step step1 center"><?php echo $messages[$lang][2]; ?></p>
<p class="step step2 center"><?php echo $messages[$lang][3]; ?></p>
<p class="step step3 center"><?php echo $messages[$lang][4]; ?></p>
<p class="step center loading"><img src="data:image/gif;base64," alt="loading..." /></p>
<p class="step step4 center"><?php echo $messages[$lang][5]; ?></p>
</div>
<footer>copyright &copy; <?php echo date("Y");?> <a href="http://www.xross-cube.com/">XROSS CUBE, Inc.</a> All Rights Reserved.</footer>
<script src="//ajax.googleapis.com/ajax/libs/jquery/2.1.1/jquery.min.js"></script>
<script>
jQuery(function(){
    $(".start").click(function(){
        $(this).css("opacity",0);
        sendRequest(1);
        return false;
    });
});
function sendRequest(stepNum){
        $(".step"+stepNum).css("opacity",1);
        $(".loading").css("opacity",1);
        $.ajax({
            "cache":false,
            "data":{"step":stepNum},
            "dataType":"json",
            "url":"./c5downloader.php",
            "success":function(d,t){
                if(d == 1 ){
                    $(".step"+(stepNum+1)).css("opacity",1);
                    if(stepNum <= 1){
                        sendRequest((stepNum+1));
                    }else{
                        $(".loading").css("opacity",0);
                        setTimeout('gotoInstallPage()', 1000);
                    }
                }else{
                    $(".loading").css("opacity",0);
                    $(".step4").css("opacity",1);
                }
            },
            "error":function(){
                $(".loading").css("opacity",0);
                $(".step4").css("opacity",1);
            }
        });
}
function gotoInstallPage(){
    if(!alert("<?php echo $messages[$lang][6]; ?>")){
        location.href="./index.php";
    }
}
</script>
</body>
